<script setup lang="ts">
import {ToothPosition, ToothStateModel} from "~/modules/tooth/models/ToothStateModel";

const {states} = withDefaults(defineProps<{states?:ToothStateModel[]}>(),{
  states:()=>[]
})
const getColor = (position:ToothPosition) => {
  const state = states.find(t => t.position == position);
  if(!state) return "#C2c2c2";
  return state.color;
}
  const emit = defineEmits(["show-tooth"]);
  const showTooth = (position:number) => {
    emit("show-tooth", position)
  }
</script>


